-- ギフトコードの作成
SELECT
  'code_' || printf('%03d', rowid) as id,
  'GIFT' || printf('%03d', rowid) as code,
  1000 as amount,
  false as isUsed,
  CURRENT_TIMESTAMP as createdAt
FROM (
  WITH RECURSIVE cnt(x) AS (
    SELECT 1
    UNION ALL
    SELECT x+1 FROM cnt
    LIMIT 100
  )
  SELECT x FROM cnt
);